parameters.jsCode:
// Calculate trial dates
const now = new Date();
const trialEnd = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);

// Extract Calendly data
const calendlyData = $input.item.json;

return {
  name: calendlyData.invitee.name,
  email: calendlyData.invitee.email,
  phone: calendlyData.invitee.phone || '',
  business_type: calendlyData.questions_and_answers?.[0]?.answer || 'Unknown',
  plan_type: 'trial',
  trial_start_date: now.toISOString(),
  trial_end_date: trialEnd.toISOString(),
  status: 'trial_active',
  created_at: now.toISOString()
};
